    public  JPanel_Hosgeldiniz() {
        
        TimerTask gorev = new TimerTask()
        {  
            int i=4;
            public void run()
            {
               i--;
               jLabel_Sayac.setText(String.valueOf(i));
               if(i==0)
               {
                 BiletUygulamasi.PanelMenu.setVisible(true);
                 BiletUygulamasi.acikOlanPanel=BiletUygulamasi.PanelMenu;
                 setVisible(false);
                 this.cancel();
               }
            }
        };
        Timer saat = new Timer();
        saat.schedule(gorev, 1000,1000);
        
        initComponents();
        
    }
   
